import React, { useState } from "react";
import { useLocation } from "react-router-dom";

const sampleData = {
  project: "Gaming Setup",
  categories: [
    {
      name: "Keyboards",
      color: "#4A90E2",
      products: [
        {
          name: "Mechanical Keyboard RGB",
          price: 69.99,
          image: "https://via.placeholder.com/200x150",
        },
        {
          name: "Wireless Gaming Keyboard",
          price: 89.99,
          image: "https://via.placeholder.com/200x150",
        },
      ],
    },
    {
      name: "Headsets",
      color: "#E94E77",
      products: [
        {
          name: "Surround Sound Headset",
          price: 59.99,
          image: "https://via.placeholder.com/200x150",
        },
        {
          name: "Wireless Gaming Headphones",
          price: 79.99,
          image: "https://via.placeholder.com/200x150",
        },
      ],
    },
  ],
};

export default function Results() {
  const location = useLocation();
  const query = location.state?.query || sampleData.project;

  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  const handleAddToCart = (product) => {
    setCart((prev) => [...prev, product]);
    setTotal((prev) => prev + product.price);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white px-6 py-10">
      <h1 className="text-3xl font-semibold text-center mb-4">
        Results for: <span className="text-red-600">{query}</span>
      </h1>
      <p className="text-center text-gray-500 mb-10">
        We found some great items for you, grouped by need and style.
      </p>

      {sampleData.categories.map((category, index) => (
        <div key={index} className="mb-12">
          <h2
            className="text-xl font-semibold mb-4"
            style={{ color: category.color }}
          >
            {category.name}
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {category.products.map((product, idx) => (
              <div
                key={idx}
                className="border rounded-2xl p-4 bg-white hover:shadow-lg transition shadow-sm"
                style={{ backgroundColor: "#f9f9fb" }}
              >
                <div className="overflow-hidden rounded mb-3">
                  <img
                    src={product.image}
                    alt={product.name}
                    className="w-full h-40 object-cover rounded transform hover:scale-105 transition duration-300"
                  />
                </div>
                <h3 className="text-md font-medium text-gray-900">
                  {product.name}
                </h3>
                <p className="text-sm text-gray-600">â‚¬{product.price}</p>
                <button
                  className="mt-3 w-full py-2 text-sm text-white bg-gradient-to-r from-neutral-800 to-neutral-900 rounded-md hover:opacity-90 transition"
                  onClick={() => handleAddToCart(product)}
                >
                  âž• Add to cart
                </button>
              </div>
            ))}
          </div>
        </div>
      ))}

      {/* Cart and Checkout */}
      <div className="fixed bottom-4 right-4 flex items-center space-x-4">
        <div className="bg-gradient-to-r from-neutral-700 to-neutral-900 text-white px-4 py-2 rounded-full shadow-lg text-sm">
          Total: â‚¬{total.toFixed(2)}
        </div>
        <button className="bg-white text-neutral-900 border border-neutral-300 px-4 py-2 rounded-full text-sm hover:bg-neutral-100 transition shadow-sm">
          Go to Checkout
        </button>
      </div>
    </div>
  );
}
