import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import Results from "./Results";

function HomePage() {
  const [input, setInput] = useState("");
  const navigate = useNavigate();

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && input.trim()) {
      navigate("/results");
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-white text-black px-4">
      <h1 className="text-4xl md:text-5xl font-bold mb-4">
        Welcome to <span className="text-gradient">SW<span className="text-red-600">AI</span></span>
      </h1>
      <p className="text-center text-gray-600 max-w-xl mb-6">
        Shop With AI — describe your project and budget, and we’ll find the best products for you. All in one click.
      </p>

      <input
        type="text"
        placeholder="What do you need?"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={handleKeyDown}
        className="border-b-2 border-gray-200 focus:outline-none focus:border-black w-80 text-center py-2 mb-4"
      />

      <p className="italic text-sm text-gray-500 mt-2 fade-text">
        Discover smart solutions based on your ideas, organized by category.
      </p>

      <p className="text-sm mt-4 text-neutral-500 tracking-wide">
        <span className="text-neutral-600">Storytrip Project</span>
      </p>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/results" element={<Results />} />
      </Routes>
    </Router>
  );
}
