import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaKeyboard, FaHeadphones } from "react-icons/fa";

const iconMap = {
  Keyboards: <FaKeyboard className="text-red-600 text-lg mr-2" />,
  Headsets: <FaHeadphones className="text-red-600 text-lg mr-2" />,
};

const Results = () => {
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);

  const resultsData = [
    {
      category: "Keyboards",
      products: [
        {
          id: 1,
          name: "Mechanical Keyboard RGB",
          price: "€69.99",
          image:
            "https://images.unsplash.com/photo-1587202372775-a7dce39f5795?w=400&q=80",
        },
        {
          id: 2,
          name: "Wireless Gaming Keyboard",
          price: "€89.99",
          image:
            "https://images.unsplash.com/photo-1614680376408-81e83d90b844?w=400&q=80",
        },
      ],
    },
    {
      category: "Headsets",
      products: [
        {
          id: 3,
          name: "Surround Sound Headset",
          price: "€49.99",
          image:
            "https://images.unsplash.com/photo-1580894732444-8ecdedc00546?w=400&q=80",
        },
        {
          id: 4,
          name: "Wireless Gaming Headphones",
          price: "€59.99",
          image:
            "https://images.unsplash.com/photo-1580894732384-2f2f58f59a91?w=400&q=80",
        },
      ],
    },
  ];

  const addToCart = (product) => {
    setCart((prev) => [...prev, product]);
  };

  const total = cart.reduce((sum, item) => {
    const price = parseFloat(item.price.replace("€", ""));
    return sum + price;
  }, 0);

  return (
    <div className="results-page px-8 py-6 max-w-7xl mx-auto">
      <div className="text-center mb-8">
        <h1 className="text-2xl font-bold bg-gradient-to-r from-black via-red-600 to-black text-transparent bg-clip-text animate-soft-fade">
          Here's what SWAI found based on your project.
        </h1>
        <p className="text-gray-500 text-sm mt-2">
          We found some great items for you, grouped by category.
        </p>
      </div>

      {resultsData.map((categoryData, index) => (
        <div key={index} className="mb-12">
          <div className="flex items-center mb-4">
            {iconMap[categoryData.category] || null}
            <h2 className="text-xl font-semibold">{categoryData.category}</h2>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
            {categoryData.products.map((product) => (
              <div
                key={product.id}
                className="border rounded-lg p-3 shadow-sm hover:shadow-md transition duration-200 text-sm"
              >
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full h-28 object-cover rounded mb-2"
                />
                <h3 className="text-base font-medium mb-1">{product.name}</h3>
                <p className="text-gray-700 mb-2 text-sm">{product.price}</p>
                <button
                  onClick={() => addToCart(product)}
                  className="w-full bg-black text-white py-1.5 rounded hover:opacity-90"
                >
                  + Add to cart
                </button>
              </div>
            ))}
          </div>

          {index < resultsData.length - 1 && (
            <hr className="my-12 border-t border-gray-200" />
          )}
        </div>
      ))}

      {cart.length > 0 && (
        <div className="fixed bottom-4 right-4 flex items-center gap-3">
          <div className="bg-black text-white px-4 py-2 rounded-lg shadow text-sm">
            Total: €{total.toFixed(2)}
          </div>
          <button
            onClick={() => navigate("/checkout")}
            className="bg-white border border-black text-black px-4 py-2 rounded-lg shadow hover:bg-gray-100 text-sm"
          >
            Go to Checkout
          </button>
        </div>
      )}
    </div>
  );
};

export default Results;

const style = document.createElement("style");
style.innerHTML = `
  @keyframes soft-fade {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.85; }
  }
  .animate-soft-fade {
    animation: soft-fade 5s ease-in-out infinite;
  }
`;
document.head.appendChild(style);
