import React, { useState } from "react";

export default function App() {
  const [query, setQuery] = useState("");

  const handleSearch = (e) => {
    e.preventDefault();
    console.log("User searched:", query);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-start bg-white text-black px-4 pt-32">
      <h1 className="text-5xl font-bold mb-4">
        Welcome to{" "}
        <span className="animate-color-change font-extrabold">SWAI</span>
      </h1>

      <p className="text-lg text-gray-600 text-center max-w-xl mb-10">
        Shop With AI — describe your project and budget, and we’ll build a full setup for you,
        from screens to accessories. All in one click.
      </p>

      <form onSubmit={handleSearch} className="w-full max-w-md">
        <input
          type="text"
          placeholder="What do you need?"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="w-full px-0 py-2 border-b-2 border-gray-400 focus:border-red-500 focus:outline-none text-center"
        />
      </form>
    </div>
  );
}
