import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import { useState } from "react";
import Results from "./Results";
import "./index.css";

function HomePage() {
  const [query, setQuery] = useState("");
  const navigate = useNavigate();

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && query.trim() !== "") {
      navigate("/results");
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-white text-black px-4">
      <h1 className="text-4xl sm:text-5xl font-bold mb-4 text-center">
        Welcome to <span className="text-fade">SWAI</span>
      </h1>
      <p className="text-center text-gray-600 max-w-xl mb-6">
        Shop With AI ‚Äî describe your project and budget, and we‚Äôll find the best products for you. All in one click.
      </p>
      <input
        type="text"
        placeholder="What do you need?"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        onKeyDown={handleKeyDown}
        className="w-full max-w-md border-b border-gray-300 focus:outline-none focus:border-red-500 text-center py-2 mb-4"
      />
      <p className="italic text-sm text-gray-500 mb-10">
        Let us know your project, and we‚Äôll organize everything.
      </p>

      <section className="bg-gray-100 py-10 w-full text-center">
        <h2 className="text-2xl font-semibold mb-6">How it works</h2>
        <div className="flex justify-center gap-12 text-sm text-gray-600">
          <div>
            <p className="text-lg font-bold text-black mb-2">üìù Prompt</p>
            <p>Enter a short description</p>
          </div>
          <div>
            <p className="text-lg font-bold text-black mb-2">üîé Review</p>
            <p>Let the AI analyze and plan</p>
          </div>
          <div>
            <p className="text-lg font-bold text-black mb-2">üõí Purchase</p>
            <p>Order your selections</p>
          </div>
        </div>
      </section>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/results" element={<Results />} />
      </Routes>
    </Router>
  );
}
