/* Fichier : src/App.js */
import React, { useState } from "react";
import { productsByCategory } from "./data";

export default function App() {
  const [cartTotal, setCartTotal] = useState(0);

  const handleAdd = (price) => setCartTotal(cartTotal + price);
  const handleRemove = (price) => setCartTotal(cartTotal - price);

  return (
    <div className="min-h-screen bg-white text-black font-sans">
      <header className="p-6 border-b border-gray-200">
        <h1 className="text-3xl font-bold">SWAI</h1>
        <input
          type="text"
          placeholder="Search anything you need..."
          className="mt-4 w-full p-2 border rounded"
        />
        <p className="mt-2 text-sm text-gray-600">
          Shopping with AI: Get the perfect products for your budget.
        </p>
      </header>

      <main className="p-6">
        {Object.entries(productsByCategory).map(([category, items]) => (
          <section key={category} className="mb-8">
            <h2 className="text-xl font-semibold mb-3 border-b pb-1">
              {category}
            </h2>
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
              {items.map((item, index) => (
                <div
                  key={index}
                  className="border p-3 rounded-xl shadow hover:shadow-md transition"
                >
                  <img
                    src={item.image}
                    alt={item.name}
                    className="w-full h-32 object-contain mb-2"
                  />
                  <h3 className="font-medium">{item.name}</h3>
                  <p className="text-sm text-gray-600">{item.brand}</p>
                  <p className="font-semibold">{item.price} €</p>
                  <div className="flex gap-2 mt-2">
                    <button
                      onClick={() => handleAdd(item.price)}
                      className="px-2 py-1 bg-red-500 text-white rounded"
                    >
                      +
                    </button>
                    <button
                      onClick={() => handleRemove(item.price)}
                      className="px-2 py-1 bg-gray-300 text-black rounded"
                    >
                      -
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </section>
        ))}
      </main>

      <footer className="fixed bottom-4 right-4 bg-black text-white px-4 py-2 rounded-full shadow-xl">
        Total: {cartTotal} €
      </footer>
    </div>
  );
}
