import React, { useState } from "react";
import { FaKeyboard, FaHeadphones } from "react-icons/fa";

const Results = () => {
  const [cart, setCart] = useState([]);

  const resultsData = [
    {
      category: "Keyboards",
      icon: <FaKeyboard className="text-xl mr-2" />, 
      products: [
        {
          id: 1,
          name: "Mechanical Keyboard RGB",
          price: 69.99,
          image:
            "https://images.unsplash.com/photo-1587202372775-a7dce39f5795?w=400&q=80",
        },
        {
          id: 2,
          name: "Wireless Gaming Keyboard",
          price: 89.99,
          image:
            "https://images.unsplash.com/photo-1614680376408-81e83d90b844?w=400&q=80",
        },
      ],
    },
    {
      category: "Headsets",
      icon: <FaHeadphones className="text-xl mr-2" />, 
      products: [
        {
          id: 3,
          name: "Surround Sound Headset",
          price: 49.99,
          image:
            "https://images.unsplash.com/photo-1580894732444-8ecdedc00546?w=400&q=80",
        },
        {
          id: 4,
          name: "Wireless Gaming Headphones",
          price: 59.99,
          image:
            "https://images.unsplash.com/photo-1580894732384-2f2f58f59a91?w=400&q=80",
        },
      ],
    },
  ];

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  const cartTotal = cart.reduce((total, item) => total + item.price, 0);

  return (
    <div className="results-page px-8 py-6 max-w-7xl mx-auto">
      <div className="text-center mb-8">
        <h1 className="text-2xl font-bold bg-gradient-to-r from-black via-red-600 to-black text-transparent bg-clip-text animate-soft-fade">
          Here's what SWAI found based on your project.
        </h1>
        <p className="text-gray-500 text-sm mt-2">
          We found some great items for You and your Project, grouped by category.
        </p>
      </div>

      {resultsData.map((categoryData, index) => (
        <div key={index} className="mb-12">
          <div className="flex items-center mb-4">
            {categoryData.icon}
            <h2 className="text-xl font-semibold">{categoryData.category}</h2>
          </div>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {categoryData.products.map((product) => (
              <div
                key={product.id}
                className="border rounded-lg p-3 shadow-sm hover:shadow-md transition duration-200"
              >
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full h-32 object-cover rounded-md mb-3"
                />
                <h3 className="text-md font-medium mb-1">{product.name}</h3>
                <p className="text-gray-700 mb-2">€{product.price.toFixed(2)}</p>
                <button
                  className="w-full bg-black text-white py-1 rounded hover:opacity-90 text-sm"
                  onClick={() => addToCart(product)}
                >
                  + Add to cart
                </button>
              </div>
            ))}
          </div>

          {index < resultsData.length - 1 && (
            <hr className="my-12 border-t border-gray-200" />
          )}
        </div>
      ))}

      {/* Panier - total et bouton checkout */}
      <div className="flex justify-end mt-12 items-center gap-4">
        <div className="border border-gray-300 px-4 py-2 rounded-lg bg-gray-50 shadow-sm">
          <p className="text-sm text-gray-500">Total:</p>
          <p className="text-xl font-semibold text-black">
            €{cartTotal.toFixed(2)}
          </p>
        </div>
        <button className="bg-black text-white px-5 py-2 rounded-lg text-sm hover:opacity-90 transition">
          Go to Checkout
        </button>
      </div>
    </div>
  );
};

export default Results;

// Animation douce
const style = document.createElement("style");
style.innerHTML = `
  @keyframes soft-fade {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.85; }
  }
  .animate-soft-fade {
    animation: soft-fade 6s ease-in-out infinite;
  }
`;
document.head.appendChild(style);
